import React, { Component,Fragment } from 'react';
class TodoList extends Component {
//每一个class里面都有constructor方法，且在首位
  constructor(props){
    super(props);
    //state负责存储组件里的数据
    this.state = {
      inputValue: '',
      list: []
    }
  }
//事件绑定的时候可以用bind对函数的作用域进行变更，在JSX中使用JS需要加{}
//在React中，绑定事件的时候，第二个字母大写开头，onChange,onClick...  
  render() {
    return (
      <Fragment>
        <div>

           <input value={this.state.inputValue} onChange={this.handleInputChange.bind(this)} />
           <button onChange={this.handleBtnClick.bind(this)}>提交</button>
        </div>
        <ul>
            {
              this.state.list.map((item,index)=>{
                return <li>{item}</li>
              })
            }
        </ul>
      </Fragment>
    );
  }
  //
  //对state里面的数据项进行变更，不能直接this.state.inputValue,要通过设置this.setState里面变更
  handleInputChange(e){

    this.setState({
      inputValue: e.target.value
    })
  }
  handleBtnClick(){
    this.setState({
      list: [...this.state.list, this.state.inputValue]
    })
  }
}

export default TodoList;
